mapper.update:
UPDATE A_BOARD SET 
A_CNT = A_CNT + 1
WHERE A_BNO = #{a_bno}

mapper.select:
SELECT A_BNO, A_TITLE, A_CONTENT, ID, NICK, A_LIKE_CNT
FROM A_BOARD
WHERE A_BNO = #{a_bno}

mapper.update:
UPDATE A_BOARD
SET LOCA = #{loca}, A_TYPE = #{a_type}, A_TITLE = #{a_title}, A_CONTENT = #{a_content}  
WHERE A_BNO = #{a_bno}

mapper.select:
SELECT A_BNO, A_TITLE, A_REGDATE, A_CNT, A_TYPE, NICK, LOCA
FROM(
	SELECT A_BNO, A_TITLE, A_REGDATE, A_CNT, A_TYPE, NICK, LOCA,
	ROW_NUMBER() OVER(ORDER BY A_BNO DESC) AS RNUM 
	FROM A_BOARD WHERE ID = #{id}
) A_BOARD
WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}
ORDER BY A_BNO DESC

SELECT A_BNO, A_TITLE, A_CONTENT, ID, NICK
FROM A_BOARD
WHERE A_BNO = #{a_bno}
