mapper.select:
SELECT COUNT(*)
FROM a_member
WHERE id = #{id}

SELECT COUNT(*)
FROM a_member
WHERE nick = #{nick}

SELECT COUNT(1)
FROM a_member
WHERE nick = #{nick} and id != #{id}

SELECT *
FROM a_member
WHERE id = #{id}
AND pass = #{pass}

SELECT *
FROM a_member
WHERE name = #{name}
AND email = #{email}

SELECT COUNT(*)
FROM a_member
WHERE id = #{id}
AND email = #{email}

mapper.update:
UPDATE a_member
SET pass = #{pass}
WHERE id = #{id}
AND email = #{email}

UPDATE a_member
SET pass = #{pass}
WHERE id = #{id}

mapper.delete:
DELETE FROM a_member
WHERE id = #{id}
AND pass = #{pass}
